\$scope.showConfirm = function() {
  var confirmPopup = \$ionicPopup.confirm({
    title: '${1:Title}',
    template: '${2:Its Content}'
  });
  confirmPopup.then(function(res) {
    if(res) {
      console.log('You are sure');
    } else {
      console.log('You are not sure');
    }
  });
};
